    const maxLossPerLot = maxLossValue / lotSize;
    const recommendedLots = Math.floor(riskAmount / maxLossPerLot) * lotSize;

    return {
      lots: recommendedLots,
      capitalRequired: maxLossValue * (recommendedLots / lotSize),
      riskAmount: riskAmount
    };
  };

