  const generateLiveOptionChain = async (underlying = 'NIFTY') => {
    setIsLoadingChain(true);

    const nseSymbol =
      underlying === 'BANKNIFTY'  ? 'BANKNIFTY'  :
      underlying === 'FINNIFTY'   ? 'FINNIFTY'   :
      underlying === 'MIDCPNIFTY' ? 'MIDCPNIFTY' : 'NIFTY';

    try {
      // Make sure we have a valid NSE session first
      await initNseSession();

      const res = await fetch(
        `${NSE}/api/option-chain-indices?symbol=${nseSymbol}`,
        { credentials: 'include', headers: { Accept: 'application/json' } }
      );
      if (!res.ok) throw new Error(`NSE returned ${res.status}`);

      const data = await res.json();
      if (!data?.records?.data) throw new Error('NSE: no records.data');

      const expiryDates  = data.records.expiryDates || [];
      const spotPrice    = data.records.underlyingValue || marketData.nifty.value;

      if (expiryDates.length > 0) setNseExpiryDates(expiryDates);

      const targetExpiry = selectedExpiry || expiryDates[0] || '';

      // Build strike map from NSE response
      const strikeMap = {};
      data.records.data.forEach(item => {
        if (targetExpiry && item.expiryDate !== targetExpiry) return;
        const s = item.strikePrice;
        if (!strikeMap[s]) strikeMap[s] = { strike: s, atmDistance: Math.abs(s - spotPrice) };

        if (item.CE) {
          strikeMap[s].ce = {
            premium : item.CE.lastPrice           || 0,
            iv      : (item.CE.impliedVolatility  || 0).toFixed(1),
            oi      : item.CE.openInterest        || 0,
            volume  : item.CE.totalTradedVolume   || 0,
            bid     : (item.CE.bidprice           || 0).toFixed(2),
            ask     : (item.CE.askPrice           || 0).toFixed(2),
            ltp     : (item.CE.lastPrice          || 0).toFixed(2),
            change  : (item.CE.pChange            || 0).toFixed(2),
            delta: 0, gamma: 0, theta: 0, vega: 0,
          };
        }
        if (item.PE) {
          strikeMap[s].pe = {
            premium : item.PE.lastPrice           || 0,
            iv      : (item.PE.impliedVolatility  || 0).toFixed(1),
            oi      : item.PE.openInterest        || 0,
            volume  : item.PE.totalTradedVolume   || 0,
            bid     : (item.PE.bidprice           || 0).toFixed(2),
            ask     : (item.PE.askPrice           || 0).toFixed(2),
            ltp     : (item.PE.lastPrice          || 0).toFixed(2),
            change  : (item.PE.pChange            || 0).toFixed(2),
            delta: 0, gamma: 0, theta: 0, vega: 0,
          };
        }
      });

      const chain = Object.values(strikeMap)
        .filter(r => r.ce && r.pe)
        .sort((a, b) => a.strike - b.strike);

      if (chain.length === 0) throw new Error('Empty chain after filter');

      setLiveOptionChain(chain);
      setChartData({
        oi    : chain.map(r => ({ strike: r.strike, ce: r.ce.oi / 1000,     pe: r.pe.oi / 1000     })),
        iv    : chain.map(r => ({ strike: r.strike, ce: parseFloat(r.ce.iv), pe: parseFloat(r.pe.iv) })),
        volume: chain.map(r => ({ strike: r.strike, ce: r.ce.volume / 1000, pe: r.pe.volume / 1000 })),
        priceHistory: [],
      });

      // Update spot price from NSE
      if (spotPrice) {
        setMarketData(prev => ({
          ...prev,
          nifty    : nseSymbol === 'NIFTY'      ? { ...prev.nifty,     value: Math.round(spotPrice) } : prev.nifty,
          bankNifty: nseSymbol === 'BANKNIFTY'  ? { ...prev.bankNifty, value: Math.round(spotPrice) } : prev.bankNifty,
        }));
      }

    } catch (err) {
      console.warn('NSE option chain fetch failed, using simulation fallback:', err.message);

      // â”€â”€ Simulation fallback (runs if proxy/NSE is unavailable) â”€â”€
      const currentPrice =
        underlying === 'BANKNIFTY' ? marketData.bankNifty.value : marketData.nifty.value;
      const step      = underlying === 'BANKNIFTY' ? 100 : 50;
      const atmStrike = Math.round(currentPrice / step) * step;
      const strikes   = Array.from({ length: 21 }, (_, i) => atmStrike + (i - 10) * step);

      const chain = strikes.map(strike => {
        const dist     = Math.abs(strike - currentPrice);
        const isITM_CE = strike < currentPrice;
        const isITM_PE = strike > currentPrice;
        const ceIV = 15 + (dist / currentPrice) * 100;
        const peIV = 15 + (dist / currentPrice) * 100;
        const cePremium = isITM_CE
          ? (currentPrice - strike) + strike * ceIV / 100 * Math.sqrt(7 / 365)
          : strike * ceIV / 100 * Math.sqrt(7 / 365);
        const pePremium = isITM_PE
          ? (strike - currentPrice) + strike * peIV / 100 * Math.sqrt(7 / 365)
          : strike * peIV / 100 * Math.sqrt(7 / 365);
        const oiMult = Math.max(0.2, 1 - dist / (currentPrice * 0.1));
        const ceOI = Math.floor((50000 + Math.random() * 100000) * oiMult);
        const peOI = Math.floor((50000 + Math.random() * 100000) * oiMult);
        return {
          strike,
          ce: {
            premium: Math.max(0.5, cePremium).toFixed(2), iv: ceIV.toFixed(1),
            oi: ceOI, volume: Math.floor(ceOI * 0.1),
            bid: (cePremium * 0.98).toFixed(2), ask: (cePremium * 1.02).toFixed(2),
            ltp: cePremium.toFixed(2), change: (Math.random() * 20 - 10).toFixed(2),
            delta: 0, gamma: 0, theta: 0, vega: 0,
          },
          pe: {
            premium: Math.max(0.5, pePremium).toFixed(2), iv: peIV.toFixed(1),
            oi: peOI, volume: Math.floor(peOI * 0.1),
            bid: (pePremium * 0.98).toFixed(2), ask: (pePremium * 1.02).toFixed(2),
            ltp: pePremium.toFixed(2), change: (Math.random() * 20 - 10).toFixed(2),
            delta: 0, gamma: 0, theta: 0, vega: 0,
          },
          atmDistance: dist,
        };
      });

      setLiveOptionChain(chain);
      setChartData({
        oi    : chain.map(r => ({ strike: r.strike, ce: r.ce.oi / 1000,     pe: r.pe.oi / 1000     })),
        iv    : chain.map(r => ({ strike: r.strike, ce: parseFloat(r.ce.iv), pe: parseFloat(r.pe.iv) })),
        volume: chain.map(r => ({ strike: r.strike, ce: r.ce.volume / 1000, pe: r.pe.volume / 1000 })),
        priceHistory: [],
      });
    } finally {
      setIsLoadingChain(false);
    }
  };

