  // Fetch real OHLCV data from Yahoo Finance via local proxy
  const generateCandlestickData = async (symbol, timeframe) => {
    const ticker = CHART_YAHOO_MAP[symbol] || `${symbol}.NS`;
    const { interval, range } = TF_MAP[timeframe] || { interval: '5m', range: '5d' };

    try {
      const res = await fetch(
        `${YAHOO}/v8/finance/chart/${encodeURIComponent(ticker)}?interval=${interval}&range=${range}`
      );
      if (!res.ok) throw new Error(`Yahoo returned ${res.status}`);
      const json = await res.json();

      const result = json?.chart?.result?.[0];
      if (!result) throw new Error('No chart result');

      const timestamps = result.timestamp;
      const q          = result.indicators?.quote?.[0];
      if (!timestamps || !q) throw new Error('No OHLCV');

      const candles = timestamps
        .map((ts, i) => ({
          time  : ts * 1000,
          open  : (q.open[i]   ?? 0).toFixed(2),
          high  : (q.high[i]   ?? 0).toFixed(2),
          low   : (q.low[i]    ?? 0).toFixed(2),
          close : (q.close[i]  ?? 0).toFixed(2),
          volume: q.volume[i]  ?? 0,
        }))
        .filter(c => parseFloat(c.close) > 0);

      if (candles.length === 0) throw new Error('All candles filtered out');

      setCandlestickData(candles);
      setLastChartUpdate(new Date());
