  // Fallback keyword analysis when no Groq key is set
  const keywordAnalysis = (title, desc) => {
    const text = (title + ' ' + (desc || '')).toLowerCase();
    const bullish = ['rally','surge','gains','rise','bullish','positive','growth','strong','boost','jump','soar','record','upgrade','beat','outperform','recover'];
    const bearish = ['fall','drop','crash','decline','loss','bearish','negative','weak','concern','risk','fear','downgrade','miss','plunge','slide','slump','cut','disappoint'];
    let b = 0, r = 0;
    bullish.forEach(w => { if (text.includes(w)) b++; });
    bearish.forEach(w => { if (text.includes(w)) r++; });
    // Negation check: "not bullish" should flip
    if (text.includes('not ') || text.includes('no ') || text.includes('despite')) { const t = b; b = Math.max(0, b-1); }
    const sentiment = b > r ? 'bullish' : r > b ? 'bearish' : 'neutral';
    const highImpact = ['rbi','reserve bank','repo rate','budget','fii','dii','interest rate','inflation','gdp','crude oil','election','policy','government','sensex','nifty','fed','federal reserve'];
    const medImpact  = ['earnings','profit','revenue','results','quarter','sector','industry'];
    let score = 0;
    highImpact.forEach(w => { if (text.includes(w)) score += 3; });
    medImpact.forEach(w => { if (text.includes(w)) score += 1; });
    const impact = score >= 5 ? 'high' : score >= 2 ? 'medium' : 'low';
    let affectedIndex = 'Nifty 50';
    if (text.includes('bank') || text.includes('hdfc') || text.includes('icici') || text.includes('sbi') || text.includes('axis') || text.includes('kotak')) affectedIndex = 'Bank Nifty';
    else if (text.includes(' it ') || text.includes('tech') || text.includes('tcs') || text.includes('infosys') || text.includes('wipro')) affectedIndex = 'Nifty IT';
    else if (text.includes('pharma') || text.includes('drug') || text.includes('healthcare') || text.includes('cipla') || text.includes('sun pharma')) affectedIndex = 'Nifty Pharma';
    else if (text.includes('auto') || text.includes('tata motors') || text.includes('maruti') || text.includes('hero') || text.includes('bajaj auto')) affectedIndex = 'Nifty Auto';
    else if (text.includes('metal') || text.includes('steel') || text.includes('aluminium') || text.includes('copper')) affectedIndex = 'Nifty Metal';
    return { sentiment, impact, affectedIndex, reasoning: 'Keyword-based analysis (add Groq API key for AI analysis)', strategy: sentiment === 'bullish' && impact !== 'low' ? 'Bull Call Spread' : sentiment === 'bearish' && impact !== 'low' ? 'Bear Put Spread' : 'Wait and Watch', risk: impact === 'high' ? 'Medium' : 'Low', timeframe: '1-3 days', probability: impact === 'high' ? 65 : 55 };
  };

  // Groq AI analysis â€” real LLM understanding of financial news
