    if (!groqApiKey) return keywordAnalysis(title, description);
    const prompt = `You are an expert Indian stock market analyst. Analyze this news and respond ONLY with a valid JSON object, no other text.

News: "${title}"
${description ? `Details: "${description.slice(0, 300)}"` : ''}

Market: Nifty=${spotPrice}, BankNifty=${bankNiftyPrice}

JSON format (respond with ONLY this, no markdown):
{"sentiment":"bullish"|"bearish"|"neutral","impact":"high"|"medium"|"low","affectedIndex":"Nifty 50"|"Bank Nifty"|"Nifty IT"|"Nifty Pharma"|"Nifty Auto"|"Nifty Metal"|"Nifty FMCG"|"Nifty Energy","reasoning":"2-3 sentences on market impact","strategy":"Bull Call Spread"|"Bear Put Spread"|"Iron Condor"|"Long Straddle"|"Wait and Watch","strategyReasoning":"why this strategy fits","risk":"Low"|"Medium"|"High","timeframe":"Intraday"|"1-3 days"|"1 week","probability":55-80}`;

    try {
      const res = await fetch(GROQ_URL, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${groqApiKey}`, 'Content-Type': 'application/json' },
        body: JSON.stringify({ model: groqModel, messages: [{ role: 'user', content: prompt }], temperature: 0.1, max_tokens: 400 }),
      });
      if (!res.ok) throw new Error(`Groq ${res.status}`);
      const data = await res.json();
      const raw = data.choices?.[0]?.message?.content?.trim() || '{}';
      const clean = raw.replace(/```json|```/g, '').trim();
      const parsed = JSON.parse(clean);
      return { ...parsed, source: 'groq' };
