      const ceVolume = Math.floor(ceOI * (0.05 + Math.random() * 0.15));
      const peVolume = Math.floor(peOI * (0.05 + Math.random() * 0.15));

      return {
        strike,
        ce: {
          premium: Math.max(0.5, cePremium).toFixed(2),
          iv: ceIV.toFixed(1),
          oi: ceOI,
          volume: ceVolume,
          bid: (cePremium * 0.98).toFixed(2),
