  const deleteStrategy = (id) => {
    if (window.confirm('Delete this strategy?')) {
      const updated = savedStrategies.filter(s => s.id !== id);
      setSavedStrategies(updated);
      localStorage.setItem('deltabuddy-strategies', JSON.stringify(updated));
