  const findBreakEvenPoints = () => {
    const breakEvens = [];
    for (let i = 1; i < multiLegPLData.length; i++) {
      const prev = multiLegPLData[i - 1];
      const curr = multiLegPLData[i];

      if ((prev.pl < 0 && curr.pl >= 0) || (prev.pl >= 0 && curr.pl < 0)) {
        breakEvens.push(curr.spot);
      }
    }
    return breakEvens;
